var sorts = ["热歌榜", "新歌榜", "抖音榜", "电音榜"]
creatSheet();
creatdiv();

function closer(c) {
	c.classList.contains("unlock") ? (c.classList.remove("unlock"),
		c.classList.add("locked"), sessionStorage.setItem("close", false)) : (c.classList.remove("locked"),
		c.classList.add("unlock"), sessionStorage.setItem("close", true))
}

function userPcAgent() {
	return navigator.platform.indexOf("Win") == 0;
}

function creatSheet() {
	var width = document.body.clientWidth + 272;
	width = width + 'px';
	var style = '';
	style += '.musicScrollUp{-webkit-transform:translateX(' + width + ');transform:translateX(' + width +
		');transition:transform 1.5s ease-out}.musicScrollDown{-webkit-transform:translateX(0);transform:translateX(0);transition:transform 1.5s ease-out}'
	style +=
		'.unlock{position:absolute;right:0;width:24px;height:24px;background:url(data:image/png;base64,) no-repeat;z-index:100;cursor:pointer;display:inline-block;transition:1.5s ease-in}.locked{position:absolute;width:24px;height:24px;right:0;background:url(data:image/png;base64,) no-repeat;z-index:100;cursor:pointer;display:inline-block;transition:1.5s ease-in}.playdiv:hover{opacity:1;transition:all 1s;-moz-transition:all 1s;-webkit-transition:all 1s}.playdiv{opacity:.4;transition:all 1s;-moz-transition:all 1s;-webkit-transition:all 1s}';
	this.styleEl = document.createElement('style')
	this.styleEl.type = 'text/css'
	this.styleEl.innerHTML = style;
	document.getElementsByTagName('head').item(0).appendChild(this.styleEl)
}

function creatdiv() {
	var app = '<div class="skin playdiv"><span class="unlock" onclick="closer(this);"></span><div id="app"></div></div>';
	document.getElementById('cplayer').innerHTML = app;
}

function createXHR() {
	var xhr = window.XMLHttpRequest ? new XMLHttpRequest() : new ActiveXObject('Microsoft.XMLHTTP');
	return xhr;
}

function Ajax(obj) {
	var xhr = new createXHR();
	xhr.onreadystatechange = () => {
		if (xhr.readyState === 4) {
			if (xhr.status === 200) {
				obj.success(xhr.responseText);
			} else {
				obj.error(xhr.status);
			}
		}
	}
	if (obj.method === 'get') {
		xhr.open(obj.method, obj.url, obj.aync);
		xhr.send(null);
	}
};

function musicInfo(arg) {
	let list = [];
	let item = {};
	let index = Math.floor(Math.random() * sorts.length);
	let targetUrl = 'https://api.uomg.com/api/rand.music?sort=' + sorts[index] + '&format=json'
	console.info(targetUrl)
	if (userPcAgent() && sessionStorage.getItem("musics")) {
		list = JSON.parse(sessionStorage.getItem("musics"));
	}
	if (arg === "init" && list.length > 0) {
		return {
			ms: list,
			it: item
		};
	}
	var t = () => {
		Ajax({
			method: 'get',
			url: targetUrl,
			success: (res) => {
				let musics = JSON.parse(res);
				musics = musics.data;
				console.info(musics)
				item = {
					"name": musics.name,
					"artist": musics.artistsname,
					"src": musics.url,
					"poster": musics.picurl,
					"lyric": lyrics(musics.url.substring(musics.url.indexOf("=") + 1, musics.url.lastIndexOf(
						".")))
				}
				list.push(item);
				if (userPcAgent()) {
					sessionStorage.setItem("musics", JSON.stringify(list));
				}
			},
			aync: false,
			data: null
		});
	}
	if (userPcAgent() && list.length < 10) {
		t();
	} else {
		t();
	}
	return {
		ms: list,
		it: item
	};
};

function lyrics(id) {
	let lyric = "";
	Ajax({
		method: 'get',
		url: 'https://api.imjad.cn/cloudmusic/?type=lyric&id=' + id,
		success: (res) => {
			var lyrics = JSON.parse(res);
			lyric = lyrics.lrc.lyric;
		},
		aync: false,
		error: () => {
			lyric = "";
		},
		data: null
	});
	return lyric;
};

window.onload = () => {
	console.info(userPcAgent())
	const player = new cplayer({
		element: document.getElementById('app'),
		playlist: musicInfo("init").ms,
		height: 2,
		width: '',
		big: true
	});

	var new_scroll_position = 0;
	var last_scroll_position;
	var div = player.view.getPlayDiv();
	window.addEventListener('scroll', (e) => {
		last_scroll_position = window.scrollY;
		if (new_scroll_position < last_scroll_position && last_scroll_position > 50) {
			// ↓ scroll
			div.classList.remove("musicScrollDown");
			div.classList.add("musicScrollUp");
			player.view.showInfo();
		} else if (new_scroll_position > last_scroll_position) {
			// ↑ scroll
			if (sessionStorage.getItem("close") !== "false") {
				div.classList.remove("musicScrollUp");
				div.classList.add("musicScrollDown");
				player.view.showPlaylist();
			}
		}
		new_scroll_position = last_scroll_position;
	});
	player.on('ended', function() {
		let music = musicInfo("ended").it;
		if (music.hasOwnProperty("src")) {
			if (userPcAgent()) {
				player.add(music);
			} else {
				let old =player.nowplay;
				player.add(music);
				player.remove(old);
			}
		}
	});
	var count = 0;
	var interval = setInterval(function() {
		player.play();
		count++;
	}, 1000);
	setTimeout(() => {
		if (count !== 0) {
			clearInterval(interval);
		}
	}, 4000);
}
